import { apiCall } from '../../services/api'
import { SEARCH_RESULT, ADD_ERROR } from '../actionTypes'


//action to get restaurant List
function getSearchResult(data){
    return {
        type: SEARCH_RESULT,
        payload : data
    }
}
//action to add error
function addError (err){
    return {
        type: ADD_ERROR,
        payload: err
    }
}
//action to remove error

function removeError (err){
    return {
        type: REMOVE_ERROR,
        payload: err
    }
}
export function searchResults(type, userData){
    return function(dispatch) {
        return new Promise((resolve, reject) => {
            return apiCall("get", `/${type}`, userData)
                .then((data) => {
                    console.log({data})
                    dispatch(getSearchResult(data))
                    resolve();
                })
                .catch(err => {
                    console.log(err)
                    reject();
                })
        })
            }
                    
    }
