import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux'
import './App.css';
import SearchBar from './components/searchBar/searchBar'
import ResultCard from './components/resultCard/resultCard'
import { searchResults } from './store/action/index'

const restObj = {
  "id": 21307,
  "name": "Scaramouche Restaurant",
  "address": "1 Benvenuto Place",
  "city": "Toronto",
  "state": "ON",
  "area": "Toronto / SW Ontario",
  "postal_code": "M4V 2L1",
  "country": "CA",
  "phone": "4169618011",
  "lat": 43.68207,
  "lng": -79.40041,
  "price": 4,
  "reserve_url": "http://www.opentable.com/single.aspx?rid=21307",
  "mobile_reserve_url": "http://mobile.opentable.com/opentable/?restId=21307",
  "image_url": "https://www.opentable.com/img/restimages/21307.jpg"
}
function App(props) {
 const [query, setQuery] = useState();

 const getQuery = (data) =>{
  setQuery(data);
 }

  return (
    <div className="App">
      <header className="App-header">
          <h1>Search Restaurant</h1>
      </header>
        <div className="InputSelection">
            <SearchBar getQuery={getQuery} searchResults={searchResults} />
        </div>
        <div className="SearchResult">
            <ResultCard restObj={restObj}  />
        </div>
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    SearchResult: state.data
  }
}
const mapDispatchToProps = { searchResults };

export default connect(mapStateToProps, mapDispatchToProps)(App);
