import React , { useState, useEffect } from 'react';
import './searchBar.css'
import SearchDropdown from '../searchDropdown/searchDropdown'


function SearchBar (props) {
  const { getQuery, searchResults } = props
    const [item, setItem] =useState({
        city: "",
        area: "",
        address: "",
        restaurantName: ""
      })
      const getCity = (city) => {
        setItem({...item, city: city})
      }
      const getArea = (area) => {
        setItem({...item, area: area})
      }
      const getAddress = (address) => {
        setItem({...item, address: address})
      }
      const getRestaurantName = (name) => {
        setItem({...item, restaurantName: name})
      }

      // useEffect(() => {
      //   getQuery(item)
      // },[item]);
      const handleClick = () => {
        getQuery(item)
      }
    return (
        <div className="searchBar">
             <SearchDropdown itemName="Enter city" getInputSelection={getCity} />
             <SearchDropdown itemName="Enter Area" getInputSelection={getArea} />
             <SearchDropdown itemName="Enter Address" getInputSelection={getAddress} />
             <SearchDropdown itemName="Restaurant name" getInputSelection={getRestaurantName} />
             <button onClick={() => handleClick()} className="SearchButton">Search</button>
        </div>
    )
}

export default SearchBar;
