import React , { useState } from 'react';
import './searchBar.css'
import SearchDropdown from '../searchDropdown/searchDropdown'


function SearchBar (props) {
  const { searchResults } = props
    const [item, setItem] =useState({
        "city": "",
        "address": "",
        "postal_code": "",
        "name": ""
      })
      const getCity = (e) => {
        setItem({...item, city : e.target.value})
      }
      const getPostalCode = (e) => {
        setItem({...item, postal_code: e.target.value})
      }
      const getAddress = (e) => {
        setItem({...item, address: e.target.value})
      }
      const getRestaurantName = (e) => {
        setItem({...item, name: e.target.value})
      }
      const handleClick = (e) => {
          e.preventDefault()
          const type = "restaurants";
          searchResults(type, item).then(() => {
            return
          })
      }
    return (
        <div className="searchBar">
              <form>
                  {/* <SearchDropdown required={true} itemName="Enter city" getInputSelection={getCity} />
                  <SearchDropdown itemName="Enter postal code" getInputSelection={getArea} />
                  <SearchDropdown itemName="Enter Address" getInputSelection={getAddress} />
                  <SearchDropdown itemName="Restaurant name" getInputSelection={getRestaurantName} /> */}
                  <input required aria-label="Enter City" type="text" className="Input" name="city" placeholder="Enter City" onChange={(e) => getCity(e)} />
                  <input aria-label="Enter Address" type="text" className="Input" name="address" placeholder="Enter Address" onChange={(e) => getAddress(e)} />
                  <input aria-label="Enter Postal code" type="text" className="Input" name="postal_code" placeholder="Enter Postal code" onChange={(e) => getPostalCode(e)} />
                  <input aria-label="Restaurant Name" type="text" className="Input" name="name" placeholder="Restaurant Name" onChange={(e) => getRestaurantName(e)} />
                  <button onClick={(e) => handleClick(e)} className="SearchButton">Search</button>
              </form>
             
        </div>
    )
}

export default SearchBar;
