import { SEARCH_RESULT } from '../actionTypes'
import { combineReducers } from 'redux'


const matchResults =  (state = { data: "Please enter" }, action) => {
    switch(action.type){
        case SEARCH_RESULT: 
            return {
                ...state, 
               data: action.payload
            };
        default: 
            return state;
    }
}

export const rootReducer = combineReducers(
                    {matchResults}
                )
