import React , { useState, useEffect } from 'react';
import './searchBar.css'
import SearchDropdown from '../searchDropdown/searchDropdown'
function SearchBar (props) {
    const [item, setItem] =useState({
        item: ""
      })
      const getItemSelection = (item) => {
        setItem(item)
      }

    return (
        <div className="searchBar">
             <SearchDropdown itemName="Enter city" getInputSelection={getItemSelection} />
             <SearchDropdown itemName="Enter Area" getInputSelection={getItemSelection} />
             <SearchDropdown itemName="Enter Address" getInputSelection={getItemSelection} />
             <SearchDropdown itemName="Restaurant name" getInputSelection={getItemSelection} />
              <button className="SearchButton">Search</button>

        </div>
    )
}

export default SearchBar;
