// import { apiCall } from '../../services/api'
import axios from 'axios'
import { SEARCH_RESULT } from '../actionTypes'


//action to get user output
 export function getSearchResult(data){
    return {
        type: SEARCH_RESULT,
        data
    }
}

export function searchResults(params){
    return dispatch => {
            return axios.get('http://opentable.herokuapp.com/api/restaurants', {
                params: {
                    city: params.city
                }})
                .then((res) => {
                    dispatch(getSearchResult(res))
                    console.log(res)
                    resolve();
                })
                .catch(err => {
                    console.log(err)
                    reject();
                })
            }
    }
