import { apiCall } from '../../services/api'
import { SEARCH_RESULT } from '../actionTypes'


//action to get user output
function getSearchResult(data){
    return {
        type: SEARCH_RESULT,
        payload : data
    }
}

export function searchResults(){
    return function(dispatch) {
        return axios.get('http://opentable.herokuapp.com/api/stats')
                .then(({data}) => {
                    console.log(data)
                    dispatch(getSearchResult(data))
                })
                .catch(err => {
                    console.log(err)
                })
            }
                    
    }
