export const SEARCH_RESULT = "SEARCH_RESULT";

//action to get user output
export const getSearchResult = (data) => {
    return {
        type: SEARCH_RESULT,
        data
    }
}

export function searchResults(paramsData){
    return dispatch => {
        return new Promise((resolve, reject) => {
            return apiCall("get", `/restaurants`, {
                params: {
                    ...paramsData
                }
            })
                .then((responseData) => {
                    getSearchResult(responseData)
                   console.log(responseData)
                    resolve();
                })
                .catch(err => {
                    reject();
                })
        })
    }
}
