import axios from 'axios';


export function apiCall(method, path, data){
    return new Promise((resolve, reject) => {
        debugger;
        return axios[method](`https://opentable.herokuapp.com/api${path}`, {
            params: {
                ...data
            }
        })
        .then(res => {
            return resolve(res.data);
        })
        .catch(err => {
            return reject(err)
        });
    })
}
