import { SEARCH_RESULT } from '../actionTypes'
import { combineReducers } from 'redux'
const defaultState = {
    data: "Please enter valid data"
}

const matchResults =  (state = defaultState, action) => {
    switch(action.type){
        case SEARCH_RESULT: 
            return {
                data : action.data
            };
        default: 
            return state;
    }
}

export const rootReducer = combineReducers(
                    {matchResults}
                )
