import React, { useState } from 'react';
import './App.css';
import SearchDropdown from './components/searchDropdown/searcDropdown'


function App() {
  const [item, setItem] =useState({
    item: ""
  })
  const getItemSelection = (item) => {
    setItem(item)
  }
  return (
    <div className="App">
      <header className="App-header">
          <h1>Search Restaurant</h1>
      </header>
      <div className="InputSelection">
          <SearchDropdown itemName="Enter city" getItemSelection={getItemSelection} />
      </div>
    </div>
  );
}

export default App;
