import { rootReducer } from './reducer/index'
import { createStore, 
        applyMiddleware,
        compose } from "redux";
import thunk from 'redux-thunk';

export function configureStore(){
    const store = createStore(
                    rootReducer,
                    applyMiddleware(thunk)
                    );
       return store;
    }
