import { SEARCH_RESULT } from '../action/index';
import { combineReducers } from 'redux'
const defaultState = {
    data: "Please enter valid data"
}

const searchResults =  (state = defaultState, action) => {
    switch(action.type){
        case SEARCH_RESULT : 
            return {
                data : action.data
            };
        default: 
            return state;
    }
}

const rootReducer = combineReducers({
                    searchResults
                })

export default rootReducer;
