import { apiCall } from '../../services/api'

export const SEARCH_RESULT = "SEARCH_RESULT";

//action to get user output
export const getSearchResult = (data) => {
    return {
        type: SEARCH_RESULT,
        data
    }
}

export function searchResults(paramsData){
    return dispatch => {
        return new Promise((resolve, reject) => {
            debugger;

            return apiCall("get","restaurants", paramsData)

                .then(({responseData}) => {

                    dispatch(getSearchResult(responseData))
                    console.log(responseData)
                    resolve();
                })
                .catch(err => {
                    console.log(err)
                    reject();
                })
        })
    }
}
