import React , { useState } from 'react';
import './searchBar.css'
import SearchDropdown from '../searchDropdown/searchDropdown'


function SearchBar (props) {
  const { searchResults } = props
    const [item, setItem] =useState({
        "city": "",
        "address": "",
        "postal_code": "",
        "name": ""
      })
      const getCity = (city) => {
        setItem({...item, city: city})
      }
      const getArea = (area) => {
        setItem({...item, postal_code: area})
      }
      const getAddress = (address) => {
        setItem({...item, address: address})
      }
      const getRestaurantName = (name) => {
        setItem({...item, name: name})
      }
      const handleClick = () => {
        
          const type = "restaurants";
          searchResults(type, item)
       
      }
    return (
        <div className="searchBar">
              <form>
             <SearchDropdown required={true} itemName="Enter city" getInputSelection={getCity} />
             <SearchDropdown itemName="Enter postal code" getInputSelection={getArea} />
             <SearchDropdown itemName="Enter Address" getInputSelection={getAddress} />
             <SearchDropdown itemName="Restaurant name" getInputSelection={getRestaurantName} />
             <button onClick={() => handleClick()} className="SearchButton">Search</button>
             </form>
        </div>
    )
}

export default SearchBar;
