import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux'
import './App.css';
import SearchBar from './components/searchBar/searchBar'
import ResultCard from './components/resultCard/resultCard'
import { searchResults } from './store/action/index'

function App(props) {
  const { searchResults , restaurantList } = props;

  return (
    <div className="App">
      <header className="App-header">
          <h1>Search Restaurant</h1>
      </header>
        <div className="InputSelection">
            <SearchBar searchResults={searchResults} />
        </div>
        <div className="SearchResult">
          { 
          restaurantList.data.restaurants? 
            restaurantList.data.restaurants.map(restaurant => (
              <ResultCard className="ListDiv" restObj={restaurant} key={restaurant.id} />
            ))
            :
            null
          }
        </div>
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    restaurantList: state.matchResults
  }
}
const mapDispatchToProps = { searchResults };

export default connect(mapStateToProps, mapDispatchToProps)(App);
