// import { apiCall } from '../../services/api'
import axios from 'axios'
import { SEARCH_RESULT } from '../actionTypes'


//action to get user output
 export function getSearchResult(data){
    return {
        type: SEARCH_RESULT,
        data
    }
}

export function searchResults(){
    return function (dispatch) {
        return axios.get('http://opentable.herokuapp.com/api/stats')
                .then((res) => {
                    console.log(res)
                    dispatch(getSearchResult(res))
                })
                .catch(err => {
                    console.log(err)
                })
            }
                    
    }
