import axios from 'axios';


export function apiCall(method, path, params){
    return new Promise((resolve, reject) => {
        debugger;
        return axios[method](`http://opentable.herokuapp.com/api${path}`, {
            params: {
                city: params.city
            }
        })
        .then(res => {
            return resolve(res.data);
        })
        .catch(err => {
            return reject(err)
        });
    })
}
