import { apiCall } from '../../services/api'
import { SEARCH_RESULT } from '../actionTypes'


//action to get user output
export const getSearchResult = (data) => {
    return {
        type: SEARCH_RESULT,
        data
    }
}

export function searchResults(params){
    return dispatch => {
        return new Promise((resolve, reject) => {
            const method = "get";
            const path = "restaurants";
            return apiCall(method,path, params)
                .then(({responseData}) => {
                    dispatch(getSearchResult(responseData))
                    console.log(responseData)
                    resolve();
                })
                .catch(err => {
                    console.log(err)
                    reject();
                })
        })
    }
}
